import{_ as a,c as n,a as r,o as s}from"./app-CTekxecS.js";const i={};function l(e,t){return s(),n("div",null,t[0]||(t[0]=[r('<h1 id="🫒-엑스트라-버진-올리브유-extra-virgin-olive-oil" tabindex="-1"><a class="header-anchor" href="#🫒-엑스트라-버진-올리브유-extra-virgin-olive-oil"><span>🫒 엑스트라 버진 올리브유 (Extra Virgin Olive Oil)</span></a></h1><p align="center"><img src="https://health.ucdavis.edu/media-resources/contenthub/post/internet/good-food/2024/04/images-body/olive-oil-health-benefits.jpg" width="100%"></p><h2 id="정의" tabindex="-1"><a class="header-anchor" href="#정의"><span>정의</span></a></h2><ul><li><strong>엑스트라 버진 올리브유</strong>는 올리브 열매를 <strong>화학 처리 없이 물리적으로 압착</strong>하여 추출한 <strong>최상 등급</strong>의 올리브유입니다.</li><li>정제 과정을 거치지 않아서 <strong>풍미, 영양소, 향미</strong>가 그대로 보존됩니다.</li><li><strong>산도</strong>가 0.8% 이하이며, 맛이나 향에 <strong>결함이 없어야지만</strong> 이 등급을 받을 수 있습니다.</li></ul><h2 id="특징" tabindex="-1"><a class="header-anchor" href="#특징"><span>특징</span></a></h2><ul><li><strong>냉압착</strong>(cold-pressed) 방식으로 만들어져 <strong>열을 가하지 않음</strong></li><li>향이 풍부하고 색은 <strong>초록빛 또는 황금빛</strong></li><li>고온 요리에 쓰면 <strong>맛과 영양소가 손상될 수 있음</strong></li></ul><h2 id="주요-성분" tabindex="-1"><a class="header-anchor" href="#주요-성분"><span>주요 성분</span></a></h2><ul><li><strong>불포화지방산</strong> (특히 올레산 Oleic acid): 건강한 지방</li><li><strong>비타민 E, K</strong></li><li><strong>폴리페놀</strong>: 항산화 물질, 목이 따끔거리는 느낌과 함께 건강 효과를 줌</li></ul><h2 id="복용-효능" tabindex="-1"><a class="header-anchor" href="#복용-효능"><span>복용 효능</span></a></h2><h3 id="_1-심혈관-건강-증진" tabindex="-1"><a class="header-anchor" href="#_1-심혈관-건강-증진"><span>1. 심혈관 건강 증진</span></a></h3><ul><li>체내에 <strong>나쁜 콜레스테롤</strong>(LDL)을 줄이고 <strong>좋은 콜레스테롤</strong>(HDL)을 늘려줌</li><li>혈관을 건강하게 유지하여 <strong>고혈압, 심근경색, 뇌졸중 예방</strong>에 효과</li></ul><h3 id="_2-몸의-염증-줄이기" tabindex="-1"><a class="header-anchor" href="#_2-몸의-염증-줄이기"><span>2. 몸의 염증 줄이기</span></a></h3><ul><li>폴리페놀과 비타민 E가 <strong>몸속 염증을 억제</strong>하고 <strong>노화를 늦춤</strong></li><li><strong>만성 질환(예: 당뇨, 암 등) 위험도 줄여줌</strong></li></ul><h3 id="_3-소화-건강-개선" tabindex="-1"><a class="header-anchor" href="#_3-소화-건강-개선"><span>3. 소화 건강 개선</span></a></h3><ul><li>위산 분비를 조절하고, 담즙 생성을 도와 <strong>소화 기능 향상</strong></li><li>변비 예방과 장 건강 유지에 효과</li></ul><h3 id="_4-체중-증가-또는-유지" tabindex="-1"><a class="header-anchor" href="#_4-체중-증가-또는-유지"><span>4. 체중 증가 또는 유지</span></a></h3><ul><li><strong>1g당 9칼로리</strong>로 열량이 높아, 살을 찌우고 싶은 사람에게 적합</li><li>설탕처럼 <strong>혈당을 급격하게 올리지 않아</strong> 건강한 에너지 공급원</li></ul><h3 id="_5-피부와-모발-건강" tabindex="-1"><a class="header-anchor" href="#_5-피부와-모발-건강"><span>5. 피부와 모발 건강</span></a></h3><ul><li><strong>피부 보습</strong>, <strong>피부 장벽 강화</strong>, <strong>건조증 완화</strong></li><li>머리카락 윤기 유지, 두피 건강에도 도움</li></ul><h2 id="고온-가열이-안-좋은-이유" tabindex="-1"><a class="header-anchor" href="#고온-가열이-안-좋은-이유"><span>고온 가열이 안 좋은 이유</span></a></h2><ul><li>엑스트라 버진 올리브유는 <strong>가열에 약한 영양소</strong>(예: 폴리페놀, 비타민 E)를 많이 포함하고 있음</li><li><strong>180℃ 이상</strong>으로 가열하면 <ul><li>좋은 성분이 <strong>파괴되거나 감소</strong></li><li><strong>발연점</strong>을 넘으면 연기와 함께 <strong>유해 물질</strong>이 나올 수 있음</li></ul></li><li>그래서 튀김이나 볶음보다는 <strong>생으로 섭취하는 것이 가장 좋음</strong></li></ul><h2 id="섭취-방법" tabindex="-1"><a class="header-anchor" href="#섭취-방법"><span>섭취 방법</span></a></h2><ul><li>하루 <strong>1~2큰술 (15~30ml)</strong> 권장</li><li>공복에 마시거나, 샐러드에 뿌리거나, 빵 찍어 먹기 등에 활용</li><li>가열 시에는 <strong>약불</strong>로 짧게 조리하는 정도까지만 사용</li></ul><h2 id="보관-방법" tabindex="-1"><a class="header-anchor" href="#보관-방법"><span>보관 방법</span></a></h2><ul><li><strong>빛, 열, 공기</strong>에 노출되면 산패됨 (맛과 품질 저하)</li><li><strong>뚜껑을 꼭 닫아 서늘하고 어두운 곳</strong>에 보관할 것</li></ul><h2 id="요약" tabindex="-1"><a class="header-anchor" href="#요약"><span>요약</span></a></h2><table><thead><tr><th>항목</th><th>내용</th></tr></thead><tbody><tr><td>분류</td><td>가장 높은 등급의 올리브유</td></tr><tr><td>주요 성분</td><td>불포화지방산, 폴리페놀, 비타민 E·K</td></tr><tr><td>섭취량</td><td>하루 1~2 큰술</td></tr><tr><td>복용 효과</td><td>심장 건강, 항산화, 소화 개선, 체중 증가</td></tr><tr><td>주의사항</td><td>고온 가열 금지 (영양 손실, 유해 물질 생성 가능)</td></tr></tbody></table><blockquote><p><em>엑스트라 버진 올리브유는 생으로 먹을수록 <strong>몸에 더 좋습니다</strong>.</em></p></blockquote>',28)]))}const h=a(i,[["render",l]]),g=JSON.parse('{"path":"/wellness/ExtraVirginOliveOil.html","title":"🫒 엑스트라 버진 올리브유 (Extra Virgin Olive Oil)","lang":"ko-KR","frontmatter":{},"git":{"updatedTime":1749504445000,"contributors":[{"name":"김병준","username":"","email":"kmbzn24@hanyang.ac.kr","commits":2}],"changelog":[{"hash":"0bf556ce0722ed831d5cc35351f3a82c876e830d","time":1749504445000,"email":"kmbzn24@hanyang.ac.kr","author":"김병준","message":"update image"},{"hash":"5d4fc1dd33b32ef59311ad5259d3c4c30caa4a71","time":1749504313000,"email":"kmbzn24@hanyang.ac.kr","author":"김병준","message":"olive"}]},"filePathRelative":"wellness/ExtraVirginOliveOil.md"}');export{h as comp,g as data};
